    let id = props.match.params.id;
    return (
        <div className='container section book-details'>
            <div className='card z-depth-0'>
                <div className='card-content'>
                    <span className='card-title'>Book Title - {id}</span>
                    <div className='details'>
                        <span>Author first and last name</span>
                        <span>Pages</span>
                        <p>Summary</p>
                        <p>Price: 0$</p>
                    </div>
                    <button className="waves-effect  blue lighten-2 btn-small">Add to cart</button>
                </div>
                <div className='card-action grey lighten-4 grey-text'>
                    <div>Posted by Sandra</div>
                    <div>8 September, 2am</div>
                </div>
            </div>
        </div>
    )
}

