import React from 'react'
import '../components.css'
import { connect } from 'react-redux'
import { firestoreConnect } from 'react-redux-firebase'
import { compose } from 'redux'

const BookDetails = (props) => {
    let id = props.match.params.id;
    const { book } = props
    if( book ) {
        return (
            <div className='container section book-details'>
            <div className='card z-depth-0'>
                <div className='card-content'>
                    <span className='card-title'>{book.title}</span>
                    <div className='details'>
                        <span>{book.author}</span>
                        <span>{book.pages}</span>
                        <p>{book.summary}</p>
                        <p>Price: {book.price}</p>
                    </div>
                    <button className="waves-effect  blue lighten-2 btn-small">Add to cart</button>
                </div>
                <div className='card-action grey lighten-4 grey-text'>
                    <div>Posted by Sandra</div>
                    <div>8 September, 2am</div>
                </div>
            </div>
        </div>
        )
    }
    else {
        return (
            <div className='container center'>
                Loading...
            </div> 
        )
    }
}

const mapStateToProps = (state, ownProps) => {
    console.log(state)
    const id = ownProps.match.params.id
    const books = state.firestore.data.books
    const book = books ? books[id] : null
    console.log(book)
    return {
        book: book
    }
}
export default compose(
    connect(mapStateToProps),
    firestoreConnect([
        { collection: 'books'}
    ])
)(BookDetails)
