import React, { Component } from 'react'
import { connect } from 'react-redux'
import { createBook } from '../../store/actions/bookActions.js'

class AddNewBook extends Component {
    state = {
        title: '',
        authorName: '',
        publisher: '',
        pages: '',
        summary: ''
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id] : e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault() //da se zanemari default refresh na stranata so stiskanje na submit
        console.log(this.state)
    }

    render() {
        return (
            <div className='container' >
                <form onSubmit={this.handleSubmit} className='white'>
                    <h5 className='grey-text text-darken-3'> Add new book</h5>

                    <div className='input-field'>
                        <label htmlFor='title'>Title</label>
                        <input type='text' id='title' onChange={this.handleChange} />
                    </div>

                    <div className='input-field'>
                        <label htmlFor='authorName'>Author</label>
                        <input type='text' id='authorName' onChange={this.handleChange} />
                    </div>

                    <div className='input-field'>
                        <label htmlFor='publisher'>Publishing House</label>
                        <input type='text' id='publisher' onChange={this.handleChange} />
                    </div>

                
                    <div className='input-field'>
                        <label htmlFor='pages'>Number of pages</label>
                        <input type='text' id='pages' onChange={this.handleChange} />
                    </div>

                    <div className='input-field'>
                        <label htmlFor='price'>Price</label>
                        <input type='text' id='price' onChange={this.handleChange} />
                    </div>
 
                    <div className='input-field'>
                        <label htmlFor='summary'>Short summary</label>
                        <textarea id='summary' className='materialize-textarea' onChange={this.handleChange} > </textarea>
                    </div>

                    <div className='input-field'>
                        <button className='btn blue lighten-2 z-depth-0'>Add</button>
                    </div>

                </form>
            </div>
        )
    }
}

const mapDispatchToProps = () => {
    return {
        addBook: (book) => dispatch(createBook(book))
    }
}

export default connect(mapDispatchToProps) (AddNewBook)
