import React from 'react'
import { Redirect } from 'react-router-dom'
import { connect } from 'react-redux'
import SingleBookCartComponent from '../books/SingleBookCartComponent.js'

const Cart = (props) => {
    if ( !props.auth.uid ) {
        return (
            <Redirect to='/signin' />
        )
    }
    let emptyList = ''
    let components = null
    let payPrice = null
    let total = 0
    let tax = 0
    let subtotal = 0
    console.log(props.books)
    if(props.books.length < 1) {
        emptyList = <span className='card-title'>The cart is empty</span>
    }
    else {
        components = props.books.map((element) => {
            priceInt = 
            subtotal = total + element.price
            return <SingleBookCartComponent
                title = {element.title}
                price = {element.price}
            />
        })
        tax=subtotal*0.18
        total=tax+subtotal

        payPrice = <div className='card z-dept-0 single-book col s12 m6' >
                        <span>Subtotal: {subtotal}</span>
                        <span>Estimated tax: {tax}</span>
                        <hr></hr>
                        <span>Total: {total}</span>
                    </div>

    }
    return (
        <div className='dashboard container'>
            {emptyList}
            {components}
            {payPrice}
        </div>
    )
}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        auth : state.firebase.auth,
        books: state.cart.cart
    }
}

export default connect(mapStateToProps)(Cart)
