const initState = {
    books: [
        {title: 'Harry Potter', price: '150$', author: 'J.K. Rowling', postedBy: 'Sandra', pages:'500', summary:'This is a story about a wizzard.'},
        {title: 'Pride and Prejudice', price: '160$', author: 'Jane Austen', postedBy: 'Penguin', pages:'350', summary:'Humorous story of love and life among English gentility during the Georgian era.'},
        {title: 'The Great Gatsby', price: '170$', author: 'F. Scott Fitzgerald', postedBy: 'Georgi', pages:'540', summary:'The Great Gatsby is a story told by Nick Carraway, who was once Gatsbys neighbor'}
    ]
}
const bookReducer = (state = initState, action) => {

    switch(action.type){
        case 'ADD_NEW_BOOK' : {
            console.log('book added', action.book)
            return state
        }
        case 'ADD_BOOK_ERROR' : {
            console.log('add book error', action.error)
            return state
        }
        default : 
            return state
    }
}

export default bookReducer
