import React, { Component } from 'react'
import { connect } from 'react-redux'
import { signIn } from '../../store/actions/authActions.js'
import { Redirect } from 'react-router-dom'

class SignIn extends Component {
    state = {
        email: '',
        password: ''
    }

    handleChange = (e) => {
        this.setState({
            [e.target.id] : e.target.value
        })
    }
    handleSubmit = (e) => {
        e.preventDefault() //da se zanemari default refresh na stranata so stiskanje na submit
        this.props.signIn(this.state)
    }

    render() {
        if ( this.props.auth.uid ) {
            return (
                <Redirect to='/' />
            )
        }

        const {authError} = this.props
        return (
            <div className='container' >
                <form onSubmit={this.handleSubmit} className='teal lighten-5 z-depth-2'>
                    <h5 className='grey-text text-darken-3'> Sign In</h5>
                    <div className='input-field'>
                        <label htmlFor='email'>Email</label>
                        <input type='email' id='email' onChange={this.handleChange} />
                    </div>
                    <div className='input-field'>
                        <label htmlFor='password'>Password</label>
                        <input type='password' id='password' onChange={this.handleChange} />
                    </div>
                    <div className='input-field'>
                        <button className='btn blue lighten-2 z-depth-0 scale-transition scale-out'>LogIn</button>
                        <div className='red-text center'>
                            {authError ? <p>{authError}</p> : null}
                        </div>
                    </div>
                    
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        authError: state.auth.authError,
        auth : state.firebase.auth
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        signIn: (creds) => dispatch(signIn(creds))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignIn)
