export const deleteBeacauseIsBought = ( id ) => {
    return (dispatch, getState, { getFirebase, getFirestore }) => {
        console.log(id)
        const firestore = getFirestore()
        firestore.collection('books').doc(id).delete()


        dispatch ({
            type: 'ITEM_IS_BOUGHT',
            payload: id
        })
    }
}
