export const addBook = (book) => {
    return (dispatch, getState, { getFirebase, getFirestore }) => {
        //make call to database
        const firestore = getFirestore()
        firestore.collection('books').add({
            ...book, 
            postedBy: 'Geogi Sokol',
            userId: '12345678',
            createdAt: new Date()
        }).then(() => {
            dispatch({
                type: 'ADD_NEW_BOOK',
                book
            })
        }).catch((error) => {
            dispatch({
                type: 'ADD_BOOK_ERROR',
                error
            })
        }) 
    }
}
